SELECT intervallo, COUNT(*)
FROM (SELECT a.DTA_AZIONE, TRUNC (a.dta_azione,'@iagg@') AS intervallo FROM dpa_log a
where
@iop@
@periodo_iap@
@ampiezza_iap@
AND a.ID_AMM = @idAmm@
@altro@
)
GROUP BY intervallo
ORDER BY intervallo

