SELECT SYSTEM_ID,CODICE FROM
(
SELECT @index@
A.SYSTEM_ID, (case when a.num_proto is not null then a.num_proto when a.num_proto is null then a.docnumber end) as codice
FROM    PROFILE a @fromOther@
@from@
WHERE @security@
@filters@
@stampe@
@unionStampe@
)

SELECT COUNT(SYSTEM_ID) FROM
(
SELECT @index@
A.SYSTEM_ID
FROM    PROFILE a @fromOther@
@from@
WHERE @security@
@filters@
@stampe@
@unionStampe@
)

insert ALL
/*+ parallel(security,4) append */  into security (thing, accessrights, personorgroup, cha_tipo_diritto, var_note_sec, id_gruppo_trasm)
/*+ append */  into dpa_copy_log (thing, accessrights, id_ruolo_dest, cha_tipo_diritto, var_note_sec, id_ruolo_orig)
(
SELECT
DISTINCT(thing),
accessrights,
@idGruppoRuoloDestinatario@ as PERSONORGROUP,
'A' as CHA_TIPO_DIRITTO,
'ACQUISITO PER COPIA VISIBILITA' as VAR_NOTE_SEC, @idGruppoRuoloOrigine@ as id_gruppo_orig
FROM security, @objType@
WHERE
security.thing = @objType@.system_id
@projectSelect@
AND
personorgroup = @idGruppoRuoloOrigine@
AND
accessrights > 20
@condizioneDocFasc@
@condizioneIdRegistro@
@condizioneAtipicita@
@codizioneVisibilita@
--Condizione di non esistenza
AND NOT EXISTS
(
select 'x' from security s where s.thing = @objType@.system_id and s.personorgroup = @idGruppoRuoloDestinatario@  and s.Accessrights = security.accessrights
)
)

SELECT @optmizerMode@
DISTINCT(thing)
FROM security, @objType@
WHERE
security.thing = @objType@.system_id
AND
personorgroup = @idGruppoRuoloOrigine@
AND
accessrights > 20
@condizioneDocFasc@
@condizioneIdRegistro@
@condizioneAtipicita@
@codizioneVisibilita@

insert into DPA_OBJECTS_SYNC_PENDING (ID_DOC_OR_FASC, TYPE, ID_GRUPPO_TO_SYNC)
(
SELECT @optmizerMode@
DISTINCT(thing),
'@typeDocOrFasc@',
@idGruppoRuoloDestinatario@
FROM security, @objType@
WHERE
security.thing = @objType@.system_id
AND
personorgroup = @idGruppoRuoloOrigine@
AND
accessrights > 20
@condizioneDocFasc@
@condizioneIdRegistro@
@condizioneAtipicita@
@codizioneVisibilita@
--Condizione di non esistenza
AND NOT EXISTS
(
select 'x' from DPA_OBJECTS_SYNC_PENDING where ID_DOC_OR_FASC = @objType@.system_id and ID_GRUPPO_TO_SYNC = @idGruppoRuoloDestinatario@
)
)

