SELECT /*+index(c) index(b) index(a) */ A.ID_RUOLO_IN_UO, A.ID_PEOPLE, A.CHA_TIPO_OGGETTO,
A.ID_PROFILE, A.ID_PROJECT,@param1@ AS DTA_INVIO_F,
B.ID_RAGIONE, B.ID_TRASMISSIONE, B.CHA_TIPO_DEST,B.ID_CORR_GLOBALE, B.SYSTEM_ID AS ID_TRASM_SINGOLA,
@param2@ AS DTA_SCADENZA,
A.ID_PEOPLE_DELEGATO AS ID_PEOPLE_DELEGATO,
nvl(vardescribe(id_profile,'SEGNATURA_CODFASC'),vardescribe(id_project,'CODFASC')) ID_SEGN_CODFASC,
nvl(vardescribe(id_profile,'DATADOC'),vardescribe(id_project,'DATA_CREAZ')) DATA_DOC_FASC,
getdesccorr(id_corr_globale) as var_desc_corr, getpeoplename(a.id_people) as full_name, getdesccorr(a.id_ruolo_in_uo) AS desc_ruolo_mitt,
g.cha_tipo_ragione, g.var_desc_ragione, getcodruolobyidcorr(a.id_ruolo_in_uo) as cod_ruolo_mitt,
GETPEOPLEUSERID(a.id_people) as USER_ID
@otherField@
FROM DPA_TRASMISSIONE A, DPA_TRASM_SINGOLA B, dpa_ragione_trasm g
@otherTable@
@param7@
WHERE A.SYSTEM_ID=B.ID_TRASMISSIONE 
@param6@
AND g.system_id = b.id_ragione
@otherFilter@
ORDER BY @orderCondition@, B.ID_TRASMISSIONE DESC

insert ALL
/*+ parallel(security,4) append */  into security (thing, accessrights, personorgroup, cha_tipo_diritto, var_note_sec, id_gruppo_trasm, cha_copia_visibilita)
/*+ append */  into dpa_copy_log (thing, accessrights, id_ruolo_dest, cha_tipo_diritto, var_note_sec, id_ruolo_orig, cha_copia_visibilita)
(
SELECT
DISTINCT(thing),
accessrights,
@idGruppoRuoloDestinatario@ as PERSONORGROUP,
'A' as CHA_TIPO_DIRITTO,
'ACQUISITO PER COPIA VISIBILITA' as VAR_NOTE_SEC, @idGruppoRuoloOrigine@ as id_gruppo_orig, '1' as cha_copia_visibilita
FROM security, @objType@
WHERE
security.thing = @objType@.system_id
@projectSelect@
AND
personorgroup = @idGruppoRuoloOrigine@
AND
accessrights > 20
@condizioneDocFasc@
@condizioneIdRegistro@
@condizioneAtipicita@
@codizioneVisibilita@
--Condizione di non esistenza
AND NOT EXISTS
(
select 'x' from security s where s.thing = @objType@.system_id and s.personorgroup = @idGruppoRuoloDestinatario@  and s.Accessrights = security.accessrights
)
)

SELECT @optmizerMode@
DISTINCT(thing)
FROM security, @objType@
WHERE
security.thing = @objType@.system_id
AND
personorgroup = @idGruppoRuoloOrigine@
AND
accessrights > 20
@condizioneDocFasc@
@condizioneIdRegistro@
@condizioneAtipicita@
@codizioneVisibilita@

SELECT COUNT(*)
FROM dpa_deleghe d
WHERE( (d.id_people_delegato = @idPeople@ AND d.cha_in_esercizio = 1
)                             -- L'utente sta esercitando delle deleghe
OR     (    d.id_people_delegante = @idPeople@
AND d.id_ruolo_delegante = @idRole@
AND d.cha_in_esercizio = 1
)                                  -- L'utente ha delegato qualcuno
)AND d.data_decorrenza < SYSDATE
AND (d.data_scadenza > SYSDATE OR d.data_scadenza IS NULL)

insert into DPA_OBJECTS_SYNC_PENDING (ID_DOC_OR_FASC, TYPE, ID_GRUPPO_TO_SYNC)
(
SELECT @optmizerMode@
DISTINCT(thing),
'@typeDocOrFasc@',
@idGruppoRuoloDestinatario@
FROM security, @objType@
WHERE
security.thing = @objType@.system_id
AND
personorgroup = @idGruppoRuoloOrigine@
AND
accessrights > 20
@condizioneDocFasc@
@condizioneIdRegistro@
@condizioneAtipicita@
@codizioneVisibilita@
--Condizione di non esistenza
AND NOT EXISTS
(
select 'x' from DPA_OBJECTS_SYNC_PENDING where ID_DOC_OR_FASC = @objType@.system_id and ID_GRUPPO_TO_SYNC = @idGruppoRuoloDestinatario@
)
)

