SELECT NVL(P.VAR_SEGNATURA, P.DOCNUMBER) AS IDDOC, P.VAR_PROF_OGGETTO AS OGGETTO_DOCUMENTO, getsegnaturarepertorio_new(P.DOCNUMBER, TA.ID_AMM) AS SEGNATURA, OC.DESCRIZIONE AS DESCRIZIONE_CAMPO_PROFILATO, ASSTEMP.VALORE_OGGETTO_DB AS VALORE, TA.ID_AMM AS IDAMM, (select dta_ins from dpa_associazione_templates where doc_number =P.DOCNUMBER and id_oggetto in (select system_id from dpa_oggetti_custom where ID_TIPO_OGGETTO in(select system_id from dpa_tipo_oggetto where tipo='Contatore')) and ROWNUM <= 1) AS DATA_DI_REPERTORIO, ASSTEMP.DTA_ANNULLAMENTO AS DATA_ANNULLAMENTO, --C.VAR_IMPRONTA AS "Impronta", getimprontawithallegati(P.DOCNUMBER) AS "Impronta", P.DOCNUMBER AS DOCNUMBER, (SELECT TOBJ.DESCRIZIONE FROM DPA_TIPO_OGGETTO TOBJ WHERE TOBJ.SYSTEM_ID = OC.ID_TIPO_OGGETTO ) AS OBJTYPE, OCC.ENABLEDHISTORY AS ENABLEDHISTORY, OCC.ID_OGG_CUSTOM AS OBJECTID FROM profile P INNER JOIN DPA_TIPO_ATTO TA ON TA.SYSTEM_ID = P.ID_TIPO_ATTO INNER JOIN DPA_ASSOCIAZIONE_TEMPLATES ASSTEMP ON P.ID_TIPO_ATTO = ASSTEMP.ID_TEMPLATE AND ASSTEMP.DOC_NUMBER = P.DOCNUMBER INNER JOIN DPA_OGGETTI_CUSTOM OC ON ASSTEMP.ID_OGGETTO = OC.SYSTEM_ID INNER JOIN DPA_OGG_CUSTOM_COMP OCC ON OC.SYSTEM_ID = OCC.ID_OGG_CUSTOM and OCC.ID_TEMPLATE = TA.SYSTEM_ID INNER JOIN COMPONENTS C ON C.DOCNUMBER = P.DOCNUMBER INNER JOIN versions V ON V.version = (SELECT MAX (version) FROM versions WHERE versions.DOCNUMBER = P.DOCNUMBER AND version <> 0 ) AND V.DOCNUMBER = P.DOCNUMBER and V.VERSION_ID = C.VERSION_ID WHERE P.CHA_TIPO_PROTO IN ('A', 'P', 'I', 'G') /* Controllo data creazione del documento antecedente l'ultima stampa */ AND P.creation_time < ( CASE WHEN (TO_CHAR( (SELECT DTALASTPRINT FROM DPA_REGISTRI_REPERTORIO WHERE COUNTERID = @idCounter@ AND RFID @idRf@ AND REGISTRYID @idRegistry@ ), 'yyyy') = '@year@') THEN (SELECT DTALASTPRINT FROM DPA_REGISTRI_REPERTORIO WHERE COUNTERID = @idCounter@ AND RFID @idRf@ AND REGISTRYID @idRegistry@ ) ELSE TO_DATE('01/01/@year@', 'dd/MM/yyyy') END) /* nuova condizione, data modifica successiva a ultima stampa */ AND P.LAST_EDIT_DATE > ( CASE WHEN (TO_CHAR( (SELECT DTALASTPRINT FROM DPA_REGISTRI_REPERTORIO WHERE COUNTERID = @idCounter@ AND RFID @idRf@ AND REGISTRYID @idRegistry@ ), 'yyyy') = '@year@') THEN (SELECT DTALASTPRINT FROM DPA_REGISTRI_REPERTORIO WHERE COUNTERID = @idCounter@ AND RFID @idRf@ AND REGISTRYID @idRegistry@ ) ELSE TO_DATE('01/01/@year@', 'dd/MM/yyyy') END)
/*Controllo per bug documenti con contatore non valorizzato */ AND P.DOCNUMBER IN (SELECT ASSTEMP1.DOC_NUMBER FROM DPA_ASSOCIAZIONE_TEMPLATES ASSTEMP1 WHERE ID_OGGETTO = @idCounter@ AND ASSTEMP1.ID_AOO_RF IN (@registryRfList@)
AND ASSTEMP1.VALORE_OGGETTO_DB IS NOT NULL AND ASSTEMP1.DTA_INS IS NOT NULL ) /*Controllo per bug documenti modificati (in oggetto e nuove versioni) lo stesso giorno della creazione */ AND ( EXISTS ( select 'x' from dpa_associazione_templates t, profile pp where t.doc_number =pp.system_id and pp.system_id=P.DOCNUMBER and t.id_oggetto in (select system_id from dpa_oggetti_custom where ID_TIPO_OGGETTO in(select system_id from dpa_tipo_oggetto where tipo='Contatore')) and trunc(dta_ins) < trunc(PP.last_edit_date) and ROWNUM <= 1 ) /*Condizione separata per annullamento. Prende tutti i documenti annullati in seguito all'ultima stampa, senza considerare quando repertoriato */ OR EXISTS (SELECT 'x' FROM DPA_PROFIL_STO WHERE ID_PROFILE = P.DOCNUMBER AND DTA_MODIFICA > ( CASE WHEN (TO_CHAR( (SELECT DTALASTPRINT FROM DPA_REGISTRI_REPERTORIO WHERE COUNTERID = @idCounter@ AND RFID @idRf@ AND REGISTRYID @idRegistry@ ), 'yyyy') = '@year@') THEN (SELECT DTALASTPRINT FROM DPA_REGISTRI_REPERTORIO WHERE COUNTERID = @idCounter@ AND RFID @idRf@ AND REGISTRYID @idRegistry@ ) ELSE TO_DATE('01/01/@year@', 'dd/MM/yyyy') END) AND DTA_MODIFICA < TO_DATE('31/12/@year@', 'dd/MM/yyyy') )) ORDER BY DATA_DI_REPERTORIO ASC, P.DOCNUMBER ASC, ASSTEMP.ID_OGGETTO ASC

