SELECT a.*, getpeopleuserid (a.author) AS code_author, a.ext AS cha_img, getinadl (a.system_id, 'D', @idGruppo@, @idPeople@) AS in_adl, getinadl (a.system_id, 'D', @idGruppo@, 0) AS in_adlrole, getinconservazione (a.system_id,NULL,'D',@idPeople@,@idGruppo@) AS in_conservazione, getchafirmato (a.docnumber) AS cha_firmato, getchatipofirma(A.docnumber) as CHA_TIPO_FIRMA, corrcat (a.system_id, a.cha_tipo_proto) AS mitt_dest, gettestoultimanota ('D',a.system_id,@idGruppo@,@idPeople@,@idGruppo@) AS ultima_nota, getpeoplename (a.author) AS desc_autore, getdesccorr (a.id_ruolo_creatore) AS desc_ruolo_autore, getcodruolobyidcorr (a.id_ruolo_creatore) AS cod_ruolo_autore, getcodreg (a.id_registro) AS cod_registro, classcat(a.system_id) as COD_FASCICOLI, getdataarrivodoc (a.docnumber) AS data_arrivo, getdiagrammistato (a.docnumber, 'D') AS stato, corrcatbytipo(a.docnumber, a.cha_tipo_proto, 'M') as mittenti, corrcatbytipo(a.docnumber, a.cha_tipo_proto, 'D') as destinatari, getEsitoPubblicazione(a.system_id) as pubblicazione, esisteNotaVisibile('D',a.system_id,@idGruppo@,@idPeople@,@idGruppo@) AS esiste_nota, a.cha_cod_t_a, getImpronta(a.docnumber) as impronta, getNomeOriginale(a.docnumber) as nome_Originale, TO_CHAR(GetDateInADL (a.system_id, 'D', @idGruppo@, @idPeople@),'dd/mm/yyyy') AS dta_ins_adl, getEsitoSpedizione(a.system_id) AS esito_spedizione, getCountRicevuteInterop(a.system_id, '') AS count_ric_interop @contatore@ @valoriCustom@ @conservazione@ @stato_cons@ FROM (SELECT @index@ interna.*, ROWNUM rnum FROM (SELECT interna2.* FROM (SELECT /*+index (a indx_ogg_text)*/ a.system_id, a.docnumber, a.dta_annulla, a.var_prof_oggetto AS var_desc_oggetto, a.id_registro, a.cha_tipo_proto, a.cha_evidenza, a.num_anno_proto, TO_CHAR ( NVL (a.dta_proto, a.creation_time), 'dd/mm/yyyy') AS DATA, TO_CHAR (a.creation_time, 'dd/mm/yyyy') AS creation_date, a.num_proto, a.var_segnatura, TO_CHAR (a.dta_proto, 'dd/mm/yyyy') AS dta_proto, a.cha_privato, a.cha_personale, a.id_documento_principale, a.cha_in_archivio, a.id_tipo_atto AS id_tipo_atto, ta.var_desc_atto AS desc_tipo_atto, a.prot_tit AS prot_tit, a.archive_date as dta_archiviazione, a.ID_RUOLO_CREATORE, a.author, a.cha_cod_t_a, a.COD_EXT_APP, a.ext FROM PROFILE a LEFT JOIN dpa_tipo_atto ta on a.id_tipo_atto = ta.system_id WHERE EXISTS (select 'x' from security e where a.system_id=e.thing and e.PERSONORGROUP in (@idPeople@, @idGruppo@) and e.ACCESSRIGHTS>0) AND (( @tipiDoc@ ) AND nvl(A.CHA_IN_CESTINO,'0')='0' @dataFrom@ @dataTo@ AND (@simplesearchoggetto@)) union (SELECT /*+index (a )*/ a.system_id, a.docnumber, a.dta_annulla, a.var_prof_oggetto AS var_desc_oggetto, a.id_registro, a.cha_tipo_proto, a.cha_evidenza, a.num_anno_proto, TO_CHAR ( NVL (a.dta_proto, a.creation_time), 'dd/mm/yyyy') AS DATA, TO_CHAR (a.creation_time, 'dd/mm/yyyy') AS creation_date, a.num_proto, a.var_segnatura, TO_CHAR (a.dta_proto, 'dd/mm/yyyy') AS dta_proto, a.cha_privato, a.cha_personale, a.id_documento_principale, a.cha_in_archivio, a.id_tipo_atto AS id_tipo_atto, ta.var_desc_atto AS desc_tipo_atto, a.prot_tit AS prot_tit, a.archive_date as dta_archiviazione, a.ID_RUOLO_CREATORE, a.author, a.cha_cod_t_a, a.COD_EXT_APP, a.ext FROM PROFILE a LEFT JOIN dpa_tipo_atto ta on a.id_tipo_atto = ta.system_id WHERE EXISTS (select 'x' from security e where a.system_id=e.thing and e.PERSONORGROUP in (@idPeople@, @idGruppo@) and e.ACCESSRIGHTS>0) AND (( @tipiDoc@ ) AND nvl(A.CHA_IN_CESTINO,'0')='0' @dataFrom@ @dataTo@ AND (a.num_proto=@simplesearchnumber@ ) ) )
union (SELECT /*+index (a)*/ a.system_id, a.docnumber, a.dta_annulla, a.var_prof_oggetto AS var_desc_oggetto, a.id_registro, a.cha_tipo_proto, a.cha_evidenza, a.num_anno_proto, TO_CHAR ( NVL (a.dta_proto, a.creation_time), 'dd/mm/yyyy') AS DATA, TO_CHAR (a.creation_time, 'dd/mm/yyyy') AS creation_date, a.num_proto, a.var_segnatura, TO_CHAR (a.dta_proto, 'dd/mm/yyyy') AS dta_proto, a.cha_privato, a.cha_personale, a.id_documento_principale, a.cha_in_archivio, a.id_tipo_atto AS id_tipo_atto, ta.var_desc_atto AS desc_tipo_atto, a.prot_tit AS prot_tit, a.archive_date as dta_archiviazione, a.ID_RUOLO_CREATORE, a.author, a.cha_cod_t_a, a.COD_EXT_APP, a.ext FROM PROFILE a LEFT JOIN dpa_tipo_atto ta on a.id_tipo_atto = ta.system_id WHERE EXISTS (select 'x' from security e where a.system_id=e.thing and e.PERSONORGROUP in (@idPeople@, @idGruppo@) and e.ACCESSRIGHTS>0) AND (( @tipiDoc@ ) AND nvl(A.CHA_IN_CESTINO,'0')='0' @dataFrom@ @dataTo@ AND ( EXISTS (SELECT 'x' FROM dpa_doc_arrivo_par dp, dpa_corr_globali sim WHERE a.system_id = dp.id_profile AND sim.system_id = dp.id_mitt_dest and a.SYSTEM_ID = dp.ID_PROFILE AND @simplesearchmittdest@)) ) ) @fulltext@
)interna2 order by system_id desc ) interna @paging@

