SELECT e.system_id, e.id_amm, e.id_people, e.id_ruolo_in_uo,
e.cha_stato, e.var_note, e.var_descrizione,
TO_CHAR(e.data_creazione, 'DD/MM/YYYY HH24:MI:SS') AS data_creazione,
TO_CHAR(e.data_certificazione, 'DD/MM/YYYY HH24:MI:SS') AS data_certificazione,
TO_CHAR(e.data_chiusura, 'DD/MM/YYYY HH24:MI:SS') AS data_chiusura,
TO_CHAR(e.data_rifiuto, 'DD/MM/YYYY HH24:MI:SS') AS data_rifiuto,
e.cha_certificazione, e.id_profile_certificazione, e.var_note_rifiuto, 
Vardescribe(e.ID_PEOPLE, 'PEOPLENAME') ||'_'|| Vardescribe(e.id_ruolo_in_uo, 'GROUPNAME') AS RICHIEDENTE
FROM dpa_area_esibizione e
WHERE id_amm = @idAmm@
@idIst@
@certif@
@ogg@
@stato@
@filtroDate@
@visibilita@
@order@
--ORDER BY system_id

