                    if (newDoc.registro.invioRicevutaManuale.ToUpper().Equals("1"))
                    {
                        DocsPaWR.Registro[] listaRF = RegistryManager.GetListRegistriesAndRF(this.Role.systemId, "1", this.Registry.systemId);

