                    if (this.EnableStateDiagram && this.PnlScadenza.Visible && !string.IsNullOrEmpty(this.DocumentStateDiagramDataValue.Text))
                    {

                        DateTime dataInserita = Convert.ToDateTime(this.DocumentStateDiagramDataValue.Text);
