                {
                    schedaNewDoc.repositoryContext.IsDocumentoGrigio = true;
                }

                DocumentManager.setSelectedRecord(schedaNewDoc);
                this.RblTypeProtocol.Enabled = true;

                Response.Redirect("Document.aspx");
            }
        }

