                for (int j = 0; j < objQueryList[i].Length; j++)
                {
                    DocsPaVO.filtri.FiltroRicerca f = objQueryList[i][j];
                    if (f.argomento.Equals("VISUALIZZA_TOP_N_DOCUMENTI"))
                        return f.valore;
                }
            }
            return null;
        }



