                }
                else
                {
                    throw new Exception("Nessuna riga aggiornata sulla Profile");
                }
            }
            catch (Exception ex)
            {
                logger.Error(ex.Message);
                retValue = false;
            }
            logger.Info("END");
            return retValue;
        }


