                string nomeQuery = do_switchQuery(objQueryList[0]);
                if (nomeQuery.Equals(""))
                {
                    q = DocsPaUtils.InitQuery.getInstance().getQuery("S_J_PROFILE__SECURITY__OGGETTARIO2");


                }
                else
                {
                    q = DocsPaUtils.InitQuery.getInstance().getQuery(nomeQuery);
