                else
                    if (filters.IndexOf("A.CREATION_") > -1)
                    preferedIndex = "/*+index (a)*/";     //"/*+index (a indx_profile_time)*/"
                else if (filters.IndexOf("A.DTA_PROTO") > -1)
                    preferedIndex = "/*+index (a indx_profile6)*/";
